  `is_real` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '取自goods的is_real',
  `extension_code` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '商品的扩展属性，取自goods的extension_code',
