  `warehouse_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '仓库ID',
  `area_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '仓库地区ID',
  `area_city` int(10) UNSIGNED NOT NULL DEFAULT 0,
  `add_time` int(10) NOT NULL COMMENT '添加时间',
  `stages_qishu` varchar(4) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '-1' COMMENT '用户选择的当前商品的分期期数 -1:不分期 ',
  `store_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '门店ID',
  `freight` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否需要寄送',
  `tid` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '运费模板id',
  `shipping_fee` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '运费',
  `store_mobile` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '门店',
  `take_time` datetime(0) NOT NULL DEFAULT '2000-01-01 00:00:00' COMMENT '提货时间',
  `is_checked` tinyint(1) NOT NULL DEFAULT 1 COMMENT '选中状态，0未选中，1选中',
  `commission_rate` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '商品佣金比例',
  `is_invalid` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '购物车商品是否无效',
  PRIMARY KEY (`rec_id`) USING BTREE,
  INDEX `session_id`(`session_id`) USING BTREE,
  INDEX `user_id`(`user_id`) USING BTREE,
  INDEX `goods_id`(`goods_id`) USING BTREE,
  INDEX `product_id`(`product_id`) USING BTREE,
  INDEX `is_real`(`is_real`) USING BTREE,
  INDEX `parent_id`(`parent_id`) USING BTREE,
  INDEX `is_shipping`(`is_shipping`) USING BTREE,
  INDEX `ru_id`(`ru_id`) USING BTREE,
  INDEX `store_id`(`store_id`) USING BTREE,
  INDEX `freight`(`freight`) USING BTREE,
  INDEX `tid`(`tid`) USING BTREE,
  INDEX `is_checked`(`is_checked`) USING BTREE,
  INDEX `warehouse_id`(`warehouse_id`) USING BTREE,
  INDEX `area_id`(`area_id`) USING BTREE,
  INDEX `is_gift`(`is_gift`) USING BTREE,
  INDEX `rec_type`(`rec_type`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 5 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '购物车' ROW_FORMAT = Dynamic;

