DROP TABLE IF EXISTS `cyc_favourable_activity`;
CREATE TABLE `cyc_favourable_activity`  (
  `act_id` smallint(5) UNSIGNED NOT NULL AUTO_INCREMENT,
  `act_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '活动名称',
