  `act_range` tinyint(3) UNSIGNED NOT NULL COMMENT '优惠范围；0，全部商品；1，按分类；2，按品牌；3，按商品',
  `act_range_ext` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '根据优惠活动范围的不同，该处意义不同；但是都是优惠范围的约束；如，如果是商品，该处是商品的id，如果是品牌，该处是品牌的id',
  `min_amount` decimal(10, 2) UNSIGNED NOT NULL COMMENT '订单达到金额下限，才参加活动',
  `max_amount` decimal(10, 2) UNSIGNED NOT NULL COMMENT '参加活动的订单金额下限，0，表示没有上限',
  `act_type` tinyint(3) UNSIGNED NOT NULL COMMENT '参加活动的优惠方式；0，送赠品或优惠购买；1，现金减免；价格打折优惠',
  `act_type_ext` decimal(10, 2) UNSIGNED NOT NULL COMMENT '如果是送赠品，该处是允许的最大数量，0，无数量限制；现今减免，则是减免金额，单位元；打折，是折扣值，100算，8折就是80',
  `activity_thumb` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '图片',
  `gift` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '如果有特惠商品，这里是序列化后的特惠商品的id,name,price信息;取值于cyc_goods的goods_id，goods_name，价格是添加活动时填写的',
  `sort_order` tinyint(3) UNSIGNED NOT NULL DEFAULT 50 COMMENT '排序',
  `user_id` int(11) UNSIGNED NOT NULL COMMENT '商家ID（同cyc_users表user_id）',
  `rs_id` int(10) NOT NULL COMMENT '卖场ID',
  `userFav_type` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '活动类型（0-自主使用，1-全场通用）',
  `userFav_type_ext` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '使用类型扩展',
  `review_status` tinyint(1) UNSIGNED NOT NULL DEFAULT 1 COMMENT '审核状态',
  `review_content` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '审核回复内容',
  `user_range_ext` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '',
  `is_user_brand` tinyint(1) UNSIGNED NOT NULL DEFAULT 0,
  PRIMARY KEY (`act_id`) USING BTREE,
  INDEX `act_name`(`act_name`) USING BTREE,
  INDEX `user_id`(`user_id`) USING BTREE,
  INDEX `review_status`(`review_status`) USING BTREE,
  INDEX `start_time`(`start_time`) USING BTREE,
  INDEX `end_time`(`end_time`) USING BTREE,
  INDEX `act_type`(`act_type`) USING BTREE,
  INDEX `rs_id`(`rs_id`) USING BTREE,
  INDEX `userFav_type`(`userFav_type`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 5 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '促销活动表' ROW_FORMAT = Dynamic;

