  `promote_end_date` int(11) UNSIGNED NOT NULL DEFAULT 0 COMMENT '促销结束时间',
  `warn_number` tinyint(3) UNSIGNED NOT NULL DEFAULT 1 COMMENT '库存预警值',
  `keywords` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '商品关键字',
  `goods_brief` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '商品的简短描述',
