  `is_on_sale` tinyint(1) UNSIGNED NOT NULL DEFAULT 1 COMMENT '该商品是否开放销售',
  `is_alone_sale` tinyint(1) UNSIGNED NOT NULL DEFAULT 1 COMMENT '是否能单独销售',
  `is_shipping` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '0表示此商品免运费',
  `integral` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '购买金额积分',
  `add_time` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '添加时间',
