  `is_delete` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否删除到回收站',
  `is_best` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '加入推荐',
  `is_new` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '加入推荐',
  `is_hot` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '加入推荐',
  `is_promote` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否促销',
  `is_volume` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '阶梯价格',
  `is_fullcut` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '促销满减',
  `is_check` tinyint(1) UNSIGNED NULL DEFAULT NULL,
  `bonus_type_id` tinyint(3) UNSIGNED NOT NULL DEFAULT 0 COMMENT '购买该商品所能领到的红包类型',
  `last_update` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '最后更新时',
  `goods_type` smallint(5) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品所属类型id，取值表dsc_goods_type的cat_id',
  `seller_note` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '商品的商家备注',
  `give_integral` int(11) NOT NULL DEFAULT -1 COMMENT '赠送消费积分数',
  `rank_integral` int(11) NOT NULL DEFAULT -1 COMMENT '赠送等级积分数',
  `suppliers_id` smallint(5) UNSIGNED NULL DEFAULT NULL COMMENT '供应商id',
  `store_hot` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '店铺热销',
  `store_new` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '店铺新品',
  `store_best` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '店铺精品',
  `group_number` smallint(8) UNSIGNED NOT NULL DEFAULT 0 COMMENT '组合购买限制数量',
  `is_limit_buy` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否限购',
  `limit_buy_start_date` int(11) NOT NULL DEFAULT 0 COMMENT '限购开始时间',
  `limit_buy_end_date` int(11) NOT NULL DEFAULT 0 COMMENT '限购结束时间',
  `limit_buy_num` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '限购设定数量',
  `review_status` tinyint(1) NOT NULL DEFAULT 1 COMMENT '审核状态',
