  `goods_shipai` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `comments_number` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品评论数',
  `sales_volume` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品销量',
  `comment_num` int(10) UNSIGNED NOT NULL DEFAULT 0,
  `model_price` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品价格模式（0-默认，1-仓库，2-地区）',
  `model_inventory` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品库存模式（0-默认，1-仓库，2-地区）',
  `model_attr` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品属性模式（0-默认，1-仓库，2-地区）',
