  `pinyin_keyword` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '商品名称拼音',
  `goods_product_tag` varchar(2000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '商品标签',
  `goods_tag` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '商品标签',
  `stages` varchar(512) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '是否分期, 空字符串:不分期',
  `stages_rate` decimal(10, 2) NOT NULL DEFAULT 0.50 COMMENT '白条分期 费率 默认0.5% ',
  `freight` tinyint(1) UNSIGNED NOT NULL DEFAULT 2 COMMENT '商品运费模式1按固定运费2运费模板',
  `shipping_fee` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '商品运费',
  `tid` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '运费模板id',
  `goods_unit` varchar(15) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '个' COMMENT '商品单位',
  `goods_cause` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '商品退货标识',
  `commission_rate` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '分销佣金',
  `from_seller` int(11) NOT NULL DEFAULT 0 COMMENT '商家导入的商品标注来源',
  `user_brand` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '品牌统一使用平台品牌ID异步操作',
  `product_table` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'products',
  `product_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品默认勾选属性货品',
  `product_price` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '商品默认勾选属性货品价格',
  `product_promote_price` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00,
  `goods_video` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '商品视频',
  `dis_commission` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '分销佣金百分比',
  `is_distribution` tinyint(3) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品是否参与分销',
  PRIMARY KEY (`goods_id`) USING BTREE,
  INDEX `goods_sn`(`goods_sn`) USING BTREE,
  INDEX `cat_id`(`cat_id`) USING BTREE,
  INDEX `last_update`(`last_update`) USING BTREE,
  INDEX `brand_id`(`brand_id`) USING BTREE,
  INDEX `goods_weight`(`goods_weight`) USING BTREE,
  INDEX `promote_end_date`(`promote_end_date`) USING BTREE,
  INDEX `promote_start_date`(`promote_start_date`) USING BTREE,
  INDEX `goods_number`(`goods_number`) USING BTREE,
  INDEX `sort_order`(`sort_order`) USING BTREE,
  INDEX `sales_volume`(`sales_volume`) USING BTREE,
  INDEX `xiangou_start_date`(`limit_buy_start_date`) USING BTREE,
  INDEX `xiangou_end_date`(`limit_buy_end_date`) USING BTREE,
  INDEX `user_id`(`user_id`) USING BTREE,
  INDEX `is_on_sale`(`is_on_sale`) USING BTREE,
  INDEX `is_alone_sale`(`is_alone_sale`) USING BTREE,
  INDEX `is_delete`(`is_delete`) USING BTREE,
  INDEX `user_cat`(`user_cat`) USING BTREE,
  INDEX `freight`(`freight`) USING BTREE,
  INDEX `tid`(`tid`) USING BTREE,
  INDEX `review_status`(`review_status`) USING BTREE,
  INDEX `user_brand`(`user_brand`) USING BTREE,
  INDEX `from_seller`(`from_seller`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 924 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '商品信息表' ROW_FORMAT = Dynamic;

