  `is_gift` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否参加优惠活动，0，否；其他，取值于ecs_cart 的is_gift，跟其一样，是参加的优惠活动的id',
  `model_attr` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商品属性模式',
  `goods_attr_id` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '商品属性ID',
  `brand_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '品牌名称',
  `ru_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商家ID（同dsc_users表user_id）',
  `shopping_fee` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '商品运费（此字段暂时无用）',
  `warehouse_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '仓库ID',
  `area_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '仓库地区ID',
  `area_city` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '仓库城市',
  `is_single` tinyint(1) NULL DEFAULT 0 COMMENT '晒单ID（此字段暂时无用）',
  `freight` tinyint(1) UNSIGNED NOT NULL DEFAULT 0,
  `tid` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '运费模板ID',
  `drp_money` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '分销金额',
  `commission_rate` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '0' COMMENT '佣金比例',
  `stages_qishu` mediumint(5) NOT NULL DEFAULT -1 COMMENT '用户选择的当前商品的分期期数 -1:不分期',
  `product_sn` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '商品规格货号',
  `is_reality` int(1) NULL DEFAULT -1 COMMENT '正品保证',
  `is_return` int(1) NULL DEFAULT -1 COMMENT '包退服务',
  `is_fast` int(1) NULL DEFAULT -1 COMMENT '闪速配送',
  PRIMARY KEY (`rec_id`) USING BTREE,
  INDEX `goods_id`(`goods_id`) USING BTREE,
  INDEX `order_id`(`order_id`) USING BTREE,
  INDEX `ru_id`(`ru_id`) USING BTREE,
  INDEX `freight`(`freight`) USING BTREE,
  INDEX `tid`(`tid`) USING BTREE,
  INDEX `stages_qishu`(`stages_qishu`) USING BTREE,
  INDEX `user_id`(`user_id`) USING BTREE,
  INDEX `product_id`(`product_id`) USING BTREE,
  INDEX `is_real`(`is_real`) USING BTREE,
  INDEX `warehouse_id`(`warehouse_id`) USING BTREE,
  INDEX `area_id`(`area_id`) USING BTREE,
  INDEX `is_gift`(`is_gift`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 9 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '订单商品表' ROW_FORMAT = Dynamic;

