-- ----------------------------
-- Table structure for cyc_order_info
-- ----------------------------
DROP TABLE IF EXISTS `cyc_order_info`;
CREATE TABLE `cyc_order_info`  (
  `order_id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `main_order_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '区分主订单作用（0为主订单，大于0是子订单）',
  `order_sn` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '订单编号',
  `user_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '购买的会员ID（同cyc_users表user_id）',
  `order_status` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '订单状态',
  `pay_status` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '支付状态',
  `shipping_status` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '配送状态',
  `consignee` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '收货人',
  `country` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '国家',
  `province` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '省份',
  `city` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '城市',
  `district` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '地区',
  `street` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '街道',
  `address` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '收货地址',
  `zipcode` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '收货人邮政编号',
  `tel` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '收货人电话',
  `mobile` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '收货人手机号码',
  `email` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '收货人邮箱',
  `best_time` varchar(120) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '收货人收货时间',
  `sign_building` varchar(120) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '建筑物',
  `shipping_id` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '配送方式ID',
  `shipping_name` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '配送方式名称',
  `shipping_code` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '配送方式代码名称',
  `shipping_type` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '配送类型（0-配送，1-自提）',
  `shipping_fee` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '配送费用',
  `postscript` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '订单附言，由用户提交订单前填写',
  `pay_id` tinyint(3) NOT NULL DEFAULT 0 COMMENT '支付方式ID',
  `pay_name` varchar(120) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '支付方式名称',
  `how_oos` varchar(120) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '缺货处理方式，等待所有商品备齐后再发； 取消订单；与店主协商',
  `how_surplus` varchar(120) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '根据字段猜测应该是余额处理方式，程序未作这部分实现',
  `pack_name` varchar(120) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '包装名称，取值表cyc_pack',
  `card_name` varchar(120) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '贺卡的名称，取值cyc_card',
  `card_message` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '贺卡内容，由用户提交',
  `inv_payee` varchar(120) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '发票抬头，用户页面填写',
  `inv_content` varchar(120) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '发票内容，用户页面选择，取值cyc_shop_config的code字段的值为invoice_content的value',
  `goods_amount` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '商品总金额',
  `cost_amount` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '订单成本',
  `insure_fee` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '保价费用',
  `pay_fee` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '支付费用,跟支付方式的配置相关，取值表cyc_payment',
  `pack_fee` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '包装费用，取值表取值表cyc_pack',
  `card_fee` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '贺卡费用，取值cyc_card',
  `money_paid` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '已付款金额',
  `order_amount` decimal(10, 2) NOT NULL DEFAULT 0.00 COMMENT '应付款金额',
  `surplus` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '该订单使用余额的数量，取用户设定余额，用户可用余额，订单金额中最小者',
  `integral` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '使用的积分的数量，取用户使用积分，商品可用积分，用户拥有积分中最小者',
  `integral_money` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '使用积分金额',
  `bonus` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '使用红包金额',
  `return_amount` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '订单整站退款金额',
  `from_ad` smallint(5) NOT NULL DEFAULT 0 COMMENT '订单由某广告带来的广告id，应该取值于cyc_ad',
  `referer` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '订单的来源页面',
  `add_time` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '订单生成时间',
  `confirm_time` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '订单确认时间',
  `pay_time` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '订单支付时间',
  `shipping_time` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '订单配送时间',
  `confirm_take_time` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '确认收货时间',
  `auto_delivery_time` int(11) UNSIGNED NOT NULL DEFAULT 15 COMMENT '订单自动确认收货时间',
  `pack_id` tinyint(3) UNSIGNED NOT NULL DEFAULT 0 COMMENT '包装id，取值取值表cyc_pack',
  `card_id` tinyint(3) UNSIGNED NOT NULL DEFAULT 0 COMMENT '贺卡id，用户在页面选择，取值取值cyc_card',
  `bonus_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '红包的id，cyc_user_bonus的bonus_id',
  `invoice_no` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '发货单号，发货时填写，可在订单查询查看',
  `extension_code` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '通过活动购买的商品的代号；GROUP_BUY是团购；AUCTION，是拍卖；SNATCH，夺宝奇兵；正常普通产品该处为空',
  `extension_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '通过活动购买的物品的id，取值cyc_goods_activity；如果是正常普通商品，该处为0',
  `to_buyer` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '商家给客户的留言,当该字段有值时可以在订单查询看到',
  `pay_note` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '付款备注，在订单管理里编辑修改',
  `agency_id` int(10) UNSIGNED NOT NULL COMMENT '该笔订单被指派给的办事处的id，根据订单内容和办事处负责范围自动决定，也可以有管理员修改，取值于表cyc_agency',
  `inv_type` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '发票类型，用户页面选择，取值cyc_shop_config的code字段的值为invoice_type的value',
  `tax` decimal(10, 2) UNSIGNED NOT NULL COMMENT '发票税额',
  `is_separate` tinyint(1) NOT NULL DEFAULT 0 COMMENT '0，未分成或等待分成；1，已分成；2，取消分成',
  `parent_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '能获得推荐分成的用户id，id取值于表cyc_users',
  `coupons` decimal(10, 2) UNSIGNED NOT NULL DEFAULT 0.00 COMMENT '优惠券金额',
  `discount` decimal(10, 2) UNSIGNED NOT NULL COMMENT '折扣金额',
  `discount_all` decimal(10, 2) UNSIGNED NOT NULL COMMENT '分单时记录红包总金额',
  `is_delete` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '会操作删除订单状态（0为删除，1删除回收站，2会员订单列表不显示该订单信息）',
  `is_settlement` tinyint(1) NOT NULL DEFAULT 0,
  `sign_time` int(30) NULL DEFAULT NULL,
  `is_single` tinyint(1) NULL DEFAULT 0 COMMENT '自提编号',
  `point_id` smallint(8) UNSIGNED NOT NULL DEFAULT 0,
  `shipping_dateStr` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '自提时间',
  `supplier_id` int(10) NOT NULL DEFAULT 0 COMMENT '供货商ID',
  `froms` char(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'pc' COMMENT 'pc:电脑,web:手机H5,ios:苹果应用,android:安卓应用,wxapp:微信小程序',
  `uc_id` int(10) UNSIGNED NOT NULL DEFAULT 0,
  `is_zc_order` int(10) NULL DEFAULT 0 COMMENT '是否是众筹订单',
  `zc_goods_id` int(10) NOT NULL COMMENT '众筹商品ID',
  `is_frozen` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '是否冻结',
  `chargeoff_status` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '账单 (0:未结账 1:已出账 2:已结账单)',
  `invoice_type` tinyint(1) NOT NULL DEFAULT 0 COMMENT '发票类型',
  `vat_id` int(10) NOT NULL DEFAULT 0 COMMENT '增值税发票信息ID 关联 users_vat_invoices_info表自增ID',
  `tax_id` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '纳税人识别号',
  `is_update_sale` tinyint(1) UNSIGNED NOT NULL DEFAULT 0 COMMENT '判断付款时是否更新了销量,0为未更新，1为已更新',
  `ru_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '商家ID',
  PRIMARY KEY (`order_id`) USING BTREE,
  UNIQUE INDEX `order_sn`(`order_sn`) USING BTREE,
  INDEX `user_id`(`user_id`) USING BTREE,
  INDEX `order_status`(`order_status`) USING BTREE,
  INDEX `shipping_status`(`shipping_status`) USING BTREE,
  INDEX `pay_status`(`pay_status`) USING BTREE,
  INDEX `pay_id`(`pay_id`) USING BTREE,
  INDEX `extension_code`(`extension_code`, `extension_id`) USING BTREE,
  INDEX `agency_id`(`agency_id`) USING BTREE,
  INDEX `main_order_id`(`main_order_id`) USING BTREE,
  INDEX `uc_id`(`uc_id`) USING BTREE,
  INDEX `parent_id`(`parent_id`) USING BTREE,
  INDEX `supplier_id`(`supplier_id`) USING BTREE,
  INDEX `is_zc_order`(`is_zc_order`) USING BTREE,
  INDEX `zc_goods_id`(`zc_goods_id`) USING BTREE,
  INDEX `chargeoff_status`(`chargeoff_status`) USING BTREE,
  INDEX `country`(`country`) USING BTREE,
  INDEX `province`(`province`) USING BTREE,
  INDEX `city`(`city`) USING BTREE,
  INDEX `district`(`district`) USING BTREE,
  INDEX `street`(`street`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 7 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '订单表' ROW_FORMAT = Dynamic;

